#include "pch.hpp"

namespace PixelWorldEngine {
	constexpr char PixelWorldDefaultShaderCode[] = "#pragma pack_matrix(row_major)  \n \nstruct InputData \n{ \n    float3 position : POSITION; \n    float4 color : COLOR; \n    float2 tex0 : TEXCOORD0; \n}; \n \nstruct OutputData \n{ \n    float4 positionH : SV_POSITION; \n    float4 color : COLOR; \n    float2 tex0 : TEXCOORD0; \n}; \n \ncbuffer Camera : register(b0) \n{ \n    matrix project; \n}; \n \ncbuffer Transform : register(b1) \n{ \n    matrix world; \n}; \n \nOutputData vs_main(InputData input) \n{ \n    OutputData result; \n \n    result.positionH = mul(float4(input.position, 1.f), world); \n    result.positionH = mul(result.positionH, project); \n \n    result.color = input.color; \n    result.tex0 = input.tex0; \n \n    return result; \n} \n \nfloat4 ps_main(OutputData input) : SV_TARGET \n{ \n    return float4(1, 1, 1, 1); \n}\n";
	constexpr char ApplicationDefaultShaderCode[] = "#pragma pack_matrix(row_major)  \n \nstruct InputData \n{ \n    float3 position : POSITION; \n    float4 color : COLOR; \n    float2 tex0 : TEXCOORD0; \n}; \n \nstruct OutputData \n{ \n    float4 positionH : SV_POSITION; \n    float4 color : COLOR; \n    float2 tex0 : TEXCOORD0; \n}; \n \ncbuffer Camera : register(b0) \n{ \n    matrix project; \n}; \n \nTexture2D Texture : register(t0); \n \nSamplerState sample \n{ \n    Filter = MIN_MAG_MIP_LINEAR; \n    AddressU = CLAMP; \n    AddressV = CLAMP; \n}; \n \nOutputData vs_main(InputData input) \n{ \n    OutputData result; \n     \n    result.positionH = mul(float4(input.position, 1.0f), project); \n \n    result.color = input.color; \n    result.tex0 = input.tex0; \n \n    return result; \n} \n \nfloat4 ps_main(OutputData input) : SV_TARGET \n{ \n    return Texture.Sample(sample, input.tex0); \n}\n";

}